//with make styles
import React from 'react';
import {Typography, Card, CardContent } from '@mui/material'
import { makeStyles } from '@mui/styles';
import AccessTimeIcon from '@mui/icons-material/AccessTime';
import Image from './atoms/Image'
const useStyles = makeStyles(theme => ({
    wrapIcon: {
        verticalAlign: 'middle',
        display: 'inline-flex',
        fontSize: '14px',
        fontStyle: 'normal',
        lineHeight: '18px',
        letterSpacing: '0em',
        textAlign: 'left'
     },
    bookName:{
        fontSize: '18px',
        fontStyle: 'normal',
        lineHeight: '23px',
        letterSpacing: '0em',
        textAlign: 'left'
    },
    authorName:{
        fontSize: '16px',
        fontStyle: 'normal',
        lineHeight: '20px',
        letterSpacing: '0em',
        textAlign: 'left'
    },
    time:{
        fontSize: '14px',
          fontStyle: 'normal',
          fontWeight: '400',
          lineHeight: '18px',
          letterSpacing: '0em',
          textAlign: 'left'
    }
  })); 
interface Props{
    imag:string,
    name:string,
    author:string,
    duration:string
}
function Cards(props :Props) {
    const classes=useStyles()
    return (
        <div>
    <Card  sx={{ maxWidth: 284 }}>
     {/* <CardMedia 
      component="img"
      height="292"
      image={props.imag} /> */}
        <Image path={props.imag} width='100%' />

        <CardContent>
            <Typography className={classes.bookName} fontWeight='fontWeightBold' variant="subtitle1">
                {props.name}
            </Typography>

            <Typography className={classes.authorName} fontWeight='fontWeightLight' variant="body1">
                {props.author}
            </Typography>

            <div style={{
    display: 'flex',
    alignItems: 'center',
    flexWrap: 'wrap',
}}>
            <Typography variant="body2" fontWeight='fontWeightLight' className={classes.wrapIcon} >
                <AccessTimeIcon /> {props.duration}-minute read
            </Typography>
            </div>
        </CardContent>
    </Card>
        </div>
    );
}

export default Cards;
