import React from 'react'
import {Typography,Container} from '@mui/material'
import { makeStyles } from '@mui/styles';
import Footer from './Footer';
import SwitchTab from './molecules/SwitchTab';
import Navbar from './organisms/Navbar';
import Dropdown from './organisms/Dropdown';
import SearchBar from './molecules/SearchBar';
import one from './atoms/images/1.png';
import CustomAppbar from './organisms/CustomAppbar';
import Banner from './organisms/Banner';
import TrendingBlink from './TrendingBlink';
import NewlyAdded from './NewlyAdded';
import Featured from './Featured';
import { positions } from '@mui/system';
import MyTabs from './molecules/MyTabs';
import BookDetail from './BookDetail';
import FinishedBooks from './FinishedBooks';

const useStyles = makeStyles(theme => ({
  heading:{
      textAlign:"left" ,
  },
  text:{
    paddingLeft:'10%',
    paddingTop:'3%',
    paddingBottom:'3%',
    height: '30px',
    fontSize: '24px',
    fontStyle: 'normal',
    fontWeight: '700',
    lineHeight: '30px',
    textAlign: 'left',
  }
})); 

function App(){
  const classes=useStyles();
  const [state,setState]= React.useState(0)
  const changeState=(val: number)=>{
      console.log(val)
      setState(val)
      console.log(val)
      }
  return (
    <>
    <div>
     <Dropdown changeState={changeState}/>
    </div>

{ state===0 && <div> <main style={{marginLeft:"15%"}}>
        <Container maxWidth="lg">
        <Typography variant="h1" className={classes.heading} >
          My Library
        </Typography>
        </Container>
    </main> 

   { <SwitchTab />}</div> }

  { state===1 && <div>
    <Banner/>
    <div style={{marginLeft:'20%', marginTop:'2%'}}>
    <SearchBar/>
    </div>
    <Typography className={classes.text} variant='h6'>Trending blinks</Typography>
    <TrendingBlink changeState={changeState}/>
    <Typography className={classes.text} variant='h6'>Just Added</Typography>
    <NewlyAdded/>
    <Typography className={classes.text} variant='h6'>Featured Audio Links</Typography>
    <Featured />
    </div>  }

{/* {state===2 && <BookDetail changeState={changeState} />} */}

{state===0 && <BookDetail changeState={changeState} />}
{state===3 && <FinishedBooks />}
   <div style={{zIndex:0,backgroundColor:'#F1F6F4'}}>
    <Footer />
    </div>
    </>
  );
}
export default App;
