import React from 'react'
import {Typography,Container} from '@mui/material'
import { makeStyles } from '@mui/styles';
import Footer from './Footer';
import SwitchTab from './molecules/SwitchTab';
import Navbar from './organisms/Navbar';
import Dropdown from './organisms/Dropdown';
import SearchBar from './SearchBar';
import one from './atoms/images/1.png';
import CustomAppbar from './organisms/CustomAppbar';
import Banner from './organisms/Banner';

const useStyles = makeStyles(theme => ({
  heading:{
    fontSize: '36px',
      lineHeight: '45px',
      fontStyle: 'normal',
      textAlign:"left" ,
      color:"black",
      fontWeight:'700',
  }
})); 

function App(){
  const classes=useStyles();
  const [toggle,setToggle]= React.useState(0)
  const handleToggle=(e:React.MouseEventHandler<SVGSVGElement>,val: number)=>{
      console.log(val)
      setToggle(val)
      }
  return (
    <>
    <div>
    { toggle===0 && <CustomAppbar handleToggle={handleToggle}/> } 
    { toggle===1 && <Dropdown handleToggle={handleToggle}/>}

    </div>

    <main style={{marginLeft:"15%"}}>
        <Container maxWidth="lg">
        <Typography variant="h4" className={classes.heading} >
          My Library
        </Typography>
        </Container>
    </main>

    <SwitchTab/>

    <Banner/>
   <div style={{backgroundColor:'#F1F6F4'}}>
    <Footer />
    </div>
    </>
  );
}
export default App;
