import React from 'react'
import {Typography,Container, Grid} from '@mui/material'
import { makeStyles } from '@mui/styles';
import Footer from './organisms/Footer';
import SwitchTab from './molecules/SwitchTab';
import Dropdown from './organisms/Dropdown';
import SearchBar from './molecules/SearchBar';
import Banner from './organisms/Banner';
import NewlyAdded from './NewlyAdded';
import Featured from './Featured';
import BookDetail from './BookDetail';
import CardWithButton from './molecules/CardWithButton';
import being_boss from './atoms/images/being-boss.png';
import beyond from './atoms/images/beyondEntrepreneur.png';
import two from './atoms/images/2.png';
import four from './atoms/images/4.png';
import five from './atoms/images/5.png';
import six from './atoms/images/6.png';
import Cards from './molecules/Cards';

const useStyles = makeStyles(theme => ({
  heading:{
      textAlign:"left" ,
  },
  text:{
    paddingLeft:'10%',
    paddingTop:'3%',
    paddingBottom:'3%',
    height: '30px',
    fontSize: '24px',
    fontStyle: 'normal',
    fontWeight: '700',
    lineHeight: '30px',
    textAlign: 'left',
  }
})); 

function App(){
  const classes=useStyles();
  const [page,setPage]= React.useState(1)
  const changeState=(val: number)=>{
      console.log(val)
      setPage(val)
      console.log(val)
      }
  return (
    <>
    <div >
     <Dropdown changeState={changeState}/>
    </div>

{ page===1 && <div> <main style={{marginLeft:"15%"}}>
        <Container maxWidth="lg">
        <Typography variant="h1" className={classes.heading} >
          My Library
        </Typography>
        </Container>
    </main> 

   <SwitchTab /> </div> }

  { page===2 && <div>
    <Banner/>
    <div style={{marginLeft:'20%', marginTop:'2%'}}>
    <SearchBar/>
    </div>
    <Typography className={classes.text} variant='h6'>Trending blinks</Typography>
    <Container>
      <Grid container rowSpacing={2} spacing={4}>
          <Grid item md={4}>
              <CardWithButton variant='text' imag={being_boss} name="Being Boss" author="Kathleen Shannon and Emily..." duration="13"/>
          </Grid>
          <Grid item md={4}>
              <CardWithButton changeState={changeState} variant='text' imag={beyond} name="Beyond Entrepreneurship" author="Jim Collins & Bill Lazier" duration="12" noOfReads='1.9'/>
          </Grid>
          <Grid item md={4}>
          <CardWithButton variant='text' imag={two} name="Employee to Entrepreneur" author="Steve Glaveski" duration="15"  noOfReads='1.9'/>
          </Grid>

          <Grid item xs={12} sm={6} md={4}>
    <Cards key={1} imag={four} name="The Fate of Food" author="Amanda Little" duration="12" noOfReads="16"/>
    </Grid>

        <Grid item xs={12} sm={6} md={4}>
        <CardWithButton variant='text' imag={five} name="Lives of the Stoics" author="Ryan Holiday, Stephen Hansel" duration="13"/>
        </Grid>

        <Grid item xs={12} sm={6} md={4}>
        <Cards key={3} imag={six} name="Loving Your Business" author="Debbie King" duration="13"/>
        </Grid>
            </Grid> 
        </Container>
    <Typography className={classes.text} variant='h6'>Just Added</Typography>
    <NewlyAdded/>
    <Typography className={classes.text} variant='h6'>Featured Audio Links</Typography>
    <Featured />
    </div>  }

{state===2 && <BookDetail changeState={changeState} />}

{state===3 && <SwitchTab />}
   <div style={{zIndex:0,backgroundColor:'#F1F6F4'}}>
    <Footer />
    </div>
    </>
  );
}
export default App;
