import React, { useState } from 'react';
import {Typography,Grid,Button,Box, AppBar,CssBaseline ,Toolbar, Container } from '@mui/material'
import KeyboardArrowUpOutlinedIcon from '@mui/icons-material/KeyboardArrowUpOutlined';
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown'; 
import SearchOutlinedIcon from '@mui/icons-material/SearchOutlined';
import { makeStyles } from '@mui/styles';
import Image from '../../atoms/Image/Image'
import User from '../../atoms/User/User'
import Icons from '../../atoms/Icons/Icons';
import SchoolOutlinedIcon from '@mui/icons-material/SchoolOutlined';
import AccountBalanceWalletOutlinedIcon from '@mui/icons-material/AccountBalanceWalletOutlined';
import HourglassBottomIcon from '@mui/icons-material/HourglassBottom';
import FavoriteBorderOutlinedIcon from '@mui/icons-material/FavoriteBorderOutlined';
import SignalCellularAltOutlinedIcon from '@mui/icons-material/SignalCellularAltOutlined';
import ForumOutlinedIcon from '@mui/icons-material/ForumOutlined';
import TimelineOutlinedIcon from '@mui/icons-material/TimelineOutlined';
import AccountBalanceOutlinedIcon from '@mui/icons-material/AccountBalanceOutlined';
import ChurchOutlinedIcon from '@mui/icons-material/ChurchOutlined';
import CastleOutlinedIcon from '@mui/icons-material/CastleOutlined';
import TipsAndUpdatesOutlinedIcon from '@mui/icons-material/TipsAndUpdatesOutlined';
import TrackChangesOutlinedIcon from '@mui/icons-material/TrackChangesOutlined';
import AddAlertOutlinedIcon from '@mui/icons-material/AddAlertOutlined';
import ScienceOutlinedIcon from '@mui/icons-material/ScienceOutlined';
import BusinessCenterOutlinedIcon from '@mui/icons-material/BusinessCenterOutlined';
import SpaOutlinedIcon from '@mui/icons-material/SpaOutlined';
import PublicOutlinedIcon from '@mui/icons-material/PublicOutlined';
import AccessibilityNewOutlinedIcon from '@mui/icons-material/AccessibilityNewOutlined';

const useStyles = makeStyles(theme => ({
    logo:{position: 'absolute',
      paddingLeft:'0.5%',
      paddingTop:'0.5%'
    },
    search:{color: 'black',
        position: 'absolute',
        height:'20px',
        width: '20px',
        top:'20%'
    },
    explore:{
        color: 'black',
        position: 'absolute',
        textAlign: 'left',
        height:'20px',
        width: '57px',
        paddingTop:'0.5%',
        cursor:'pointer',
    },
    arrowUp:{
        height:'6.5px',
        width: '10.6px',
      color: 'black',
      position: 'absolute',
      top:'40%'
    },
    avatar: {
      position: 'absolute',
      height:'26px',
      width: '13px',
       },
    arrowDown:{
        height:'6.5px',
        width: '10.6px',
      color: 'black',
      position: 'absolute', top:'25%'
    },
    myLib:{
        color: 'black',
        position: 'absolute',
        textAlign: 'left',
        paddingTop:'0.5%',cursor:'pointer'
  },
    ul:{
        listStyleType:'none',padding:'0', margin:'0',fontWeight:'fontWeightLight'
    },
    category:{
        paddingTop:'5%',
        paddingBottom:'5%',
        borderBottom: '1px solid black',
        fontWeight:'fontWeightRegular',
  },
    category1:{
        paddingTop:'5%',
        paddingBottom:'5%',
        borderBottom: '1px solid black',
        fontWeight:'fontWeightRegular',
        color:'#116BE9'
  }
  })); 

interface Props{
    handleToggle?:any
    changeState(val:number): any
}

const Dropdown=(props:Props)=>{
    const [value,setValue]=useState(0);
    const classes=useStyles();
    const expandExplore=(e:React.MouseEvent<SVGSVGElement, MouseEvent>| React.MouseEvent<HTMLSpanElement, MouseEvent>)=>{
        value===0 ? setValue(1): setValue(0)
        console.log(value)
        }
    const nextPage=(e: React.MouseEvent<HTMLSpanElement, MouseEvent>,value: number)=>{
        props.changeState(value)
        value===0 ? setValue(1): setValue(0)
    }
    const toMyLibrary=(e:React.MouseEvent<HTMLSpanElement, MouseEvent>)=>{
        props.changeState(1)
    }
    return (
        <>
    <CssBaseline />
    <AppBar  style={{ background: '#FFFFFF'}} position='relative' elevation={0}>
      <Toolbar>
          <Container style={{marginLeft:"13%", marginTop:'1%',}}>
            <Grid container >
                <Grid item md={1.5}>
                    <Image className={classes.logo} path='logo.png' />
                </Grid>

                <Grid item md={0.5}>
                    <Icons className={classes.search}>
                        <SearchOutlinedIcon/>
                    </Icons>
                </Grid>

                <Grid item md={0.75}  sx={{
                    ':hover': {
                      borderBottom:'1px solid green'
                    },
                }}>
                <Typography className={classes.explore} 
                onClick={(e)=>expandExplore(e)} variant='body1'>Explore</Typography>

                </Grid>

                <Grid item md={0.25}>
                { value===0 && <KeyboardArrowDownIcon /* style={{ color: 'black',
                position: 'absolute',
                left:'31.69%', top:'30%'}} */ className={classes.arrowUp} /* onClick={(e)=>expandExplore(e)} *//>}
             { value===1 && <KeyboardArrowUpOutlinedIcon /* style={{ color: 'black',
                position: 'absolute',
                left:'31.69%', top:'30%'}} */ className={classes.arrowUp} /* onClick={(e)=>expandExplore(e)} *//>}
                </Grid>

                <Grid item md={0.25}>
                    </Grid>

                <Grid item md={0.75}>
                    <Typography className={classes.myLib} variant='body1' onClick={(e)=>toMyLibrary(e)}>My Library</Typography>
                </Grid>

                {<Grid item md={4}>
                </Grid>}

                <Grid item md={0.5}>
                    <User username="B" />
                </Grid>

                <Grid item md={0.5}>
                <Icons>
                    <KeyboardArrowDownIcon className={classes.arrowDown} />
                    </Icons>
                </Grid>
            </Grid>
          </Container>
      </Toolbar>
    </AppBar>

 {value===1 &&  <div style={{backgroundColor:'#F1F6F4',position:'absolute', width:'100%',zIndex:1}}>
    <Container style={{marginLeft:'15%'}}>
    <Grid container>
        <Grid item md={3}>
            <Typography className={classes.category1} variant='body1'>Explore by category</Typography>
            <ul className={classes.ul}>
                <li key={1}>
                    {/* <Button variant='text' sx={{':hover': {color: '#0365F2',},}}
                    style={{textTransform:'none',fontWeight:'400',
                    fontSize:'16px',
                    lineHeight:'24px',}} startIcon={<AddAlertOutlinedIcon/>}>Entrepreneurship</Button> */}
                    <Typography 
                    sx={{
                        ':hover': {
                          color: '#0365F2',
                        },
                      }}
                onClick={(e)=>nextPage(e,2)}    variant='body2'><Icons><AddAlertOutlinedIcon 
                sx={{ '&:hover': { color: '#0365F2' } }}
                /></Icons>Entrepreneurship</Typography>
                </li>
                <li key={2}>
                    <Typography variant='body2'><Icons><ScienceOutlinedIcon/></Icons>Science</Typography>
                </li>
                <li key={3}>
                    <Typography variant='body2'><Icons><PublicOutlinedIcon/></Icons>Economics</Typography>
                </li>
                <li key={4}>
                    <Typography variant='body2'><Icons><BusinessCenterOutlinedIcon/></Icons>Corporate Culture</Typography>
                </li>
                <li key={5}>
                    <Typography variant='body2'><Icons><AccessibilityNewOutlinedIcon/></Icons>Psychology</Typography>
                </li>
                <li key={6}>
                    <Typography variant='body2'><Icons><SpaOutlinedIcon/></Icons>Nature & Environment</Typography>
                </li>
            </ul>
        </Grid>

        <Grid item md={3}>
            <Typography className={classes.category} variant='body1'>See recently added titles</Typography>
            <ul className={classes.ul}>
                <li key={1}>
                    <Typography variant='body2'><Icons><AccountBalanceOutlinedIcon/></Icons>Politics</Typography>
                </li>
                <li key={2}>
                <Typography variant='body2'><Icons><ChurchOutlinedIcon/></Icons>Health & Nutrition</Typography>
                </li>
                <li key={3}>
                <Typography variant='body2'><Icons><CastleOutlinedIcon/></Icons>History</Typography>
                </li>
                <li key={4}>
                <Typography variant='body2'><Icons><TipsAndUpdatesOutlinedIcon/></Icons>Motivation & Inspiration</Typography>
                </li>
                <li key={5}>
                <Typography variant='body2'><Icons><HourglassBottomIcon/></Icons> Productivity</Typography>
                </li>
                <li key={6}>
                <Typography variant='body2'><Icons><TrackChangesOutlinedIcon/></Icons>Career & Success</Typography>
                </li>
            </ul>
        </Grid>

        <Grid item md={3}>
            <Typography className={classes.category} variant='body1'>See popular titles</Typography>
            <ul className={classes.ul}>
            <li key={1}>
                    <Typography variant='body2'><Icons><TimelineOutlinedIcon/></Icons>Marketing & Sales</Typography>
                </li>
                <li key={2}>
                <Typography variant='body2'><Icons><SignalCellularAltOutlinedIcon/></Icons>Personal Development</Typography>
                </li>
                <li key={3}>
                <Typography variant='body2'><Icons><ForumOutlinedIcon/></Icons>Communication Skills</Typography>
                </li>
                <li key={4}>
                <Typography variant='body2'><Icons><AccountBalanceWalletOutlinedIcon/></Icons>Money & Investments</Typography>
                </li>
                <li key={5}>
                <Typography variant='body2'><Icons><FavoriteBorderOutlinedIcon/></Icons>Sex & Relationship</Typography>
                </li>
                <li key={6}>
                <Typography variant='body2'> <Icons><SchoolOutlinedIcon/></Icons>Education</Typography>
                </li>
            </ul>
        </Grid>
    </Grid>
    </Container>

    </div>


}
        </>
    );
}

export default Dropdown;
