import React from 'react';
import {Typography, Card, CardContent, CardActions, Button } from '@mui/material'
import { makeStyles } from '@mui/styles';
import AccessTimeIcon from '@mui/icons-material/AccessTime';
import PersonOutlineOutlinedIcon from '@mui/icons-material/PersonOutlineOutlined';
import Image from '../atoms/Image'
import AddIcon from '@mui/icons-material/Add';

const useStyles = makeStyles(theme => ({
    wrapIconLeft: {
        verticalAlign: 'middle',
        display: 'inline-flex',
        fontSize: '14px',
        fontStyle: 'normal',
        lineHeight: '18px',
        letterSpacing: '0em',
        textAlign: 'left',
        paddingTop:'5%'
     },
    bookName:{
        fontSize: '18px',
        fontStyle: 'normal',
        lineHeight: '23px',
        letterSpacing: '0em',
        textAlign: 'left'
    },
    authorName:{
        fontSize: '16px',
        fontStyle: 'normal',
        lineHeight: '20px',
        letterSpacing: '0em',
        textAlign: 'left',
        paddingTop:'5%'
    },
  })); 
interface Props{
    imag:string,
    name:string,
    author:string,
    duration:string,
    noOfReads?:string,
    variant:'text'| 'contained' | 'outlined'
}
const CardWithButton=(props:Props)=> {
    const classes=useStyles()
    const val=30
    return (
        <div>
    <Card  sx={{ maxWidth: 284 }}>
     {/* <CardMedia 
      component="img"
      height="292"
      image={props.imag} /> */}
        <Image path={props.imag} width='100%' />

        <CardContent>
            <Typography className={classes.bookName} fontWeight='fontWeightBold' variant="subtitle1">
                {props.name}
            </Typography>

            <Typography className={classes.authorName} fontWeight='fontWeightLight' variant="body1">
                {props.author}
            </Typography>

            <div style={{display: 'flex', alignItems: 'center', flexWrap: 'wrap',}}>
                <Typography variant="body2" fontWeight='fontWeightLight' className={classes.wrapIconLeft} >
                    <AccessTimeIcon /> {props.duration}-minute read &ensp;&ensp;&ensp;&ensp;&ensp;
                    </Typography>
                    {props.noOfReads&&  
                    <Typography variant="body2" fontWeight='fontWeightLight' className={classes.wrapIconLeft} >  
                    <PersonOutlineOutlinedIcon /> {props.noOfReads}k reads </Typography>
                    }  

            </div>
        </CardContent>
       {/*  <CardActions> <MoreHorizIcon /></CardActions> */}
       <CardActions><Button className={classes.hover} variant={props.variant} startIcon={<AddIcon/>}>Add to Library</Button></CardActions>
        </Card>
        </div>
    );
}

export default CardWithButton;
