import React, { useRef, useState } from 'react';
import SearchOutlinedIcon from '@mui/icons-material/SearchOutlined';
import { TextField ,Grid, Menu, MenuItem, Typography, Container} from '@mui/material';
import MyCard from './MyCard';

interface Props{
}

const data=`{"books":[{"id":1,"src":"being-boss.png","name":"Being Boss", "author":"Kathleen Shannon and Emily...", "duration":"13"},
{"id":7,"src":"beyondEntrepreneur.png","name":"Beyond Entrepreneurship", "author":"Jim Collins & Bill Lazier", "duration":"12","noOfReads":"1.9"},
{"id":2,"src":"2.png","name":"Employee to Entrepreneur", "author":"Steve Glaveski", "duration":"15"},
{"id":4,"src":"4.png","name":"The Fate of Food", "author":"Amanda Little", "duration":"12","noOfReads":"16"},
{"id":5,"src":"5.png","name":"Lives of the Stoics", "author":"Ryan Holiday, Stephen Hansel", "duration":"13"},
{"id":6,"src":"6.png","name":"Loving Your Business", "author":"Debbie King", "duration":"13"}
]}`

const data2= JSON.parse(data);
const myData= data2.books;
console.log(myData)
const SearchBar=(props:Props)=> {
  const [searchTerm,setSearchTerm]= useState('')
    return (
        <>
<TextField style={{width:'750px',marginLeft:'20%', marginTop:'2%'}}  onChange={(e)=>{
  setSearchTerm(e.target.value)
}} label="Search by title or author" variant="standard" />

<Container   style={{ marginTop:"3%",paddingLeft:"7%",paddingRight:"7%"}}>
      <Grid container rowSpacing={2} >

{myData.filter((val:any)=>{
  if(searchTerm === "")
    return val 
  else if (val.name.toLowerCase().includes(searchTerm.toLowerCase()) || val.author.toLowerCase().includes(searchTerm.toLowerCase())){
    return val
  }
}).map((val:any,key:any) => {
return ( <Grid item xs={12} sm={6} md={4}>

 <MyCard progressBar='half' button='Finished' id={val.id} key={val.id} src={val.src} name={val.name} author={val.author} duration={val.duration}
noOfReads={val.noOfReads}
/>  </Grid> );

})} 
</Grid>
        </Container>
</> );

}

export default SearchBar;
