import React, { useState } from 'react';
import {Typography, Card, CardContent, Button, LinearProgress } from '@mui/material'
import { makeStyles } from '@mui/styles';
import AccessTimeIcon from '@mui/icons-material/AccessTime';
import PersonOutlineOutlinedIcon from '@mui/icons-material/PersonOutlineOutlined';
import Image from '../atoms/Image'
import AddIcon from '@mui/icons-material/Add';
import MoreHorizIcon from '@mui/icons-material/MoreHoriz';
import { createTheme, ThemeProvider } from '@mui/material/styles'; 
import BOOKS from '../books';
import COMPLETED from '../completed';

const useStyles = makeStyles(theme => ({
    wrapIconLeft: {
        verticalAlign: 'middle',
        display: 'inline-flex',
        textAlign: 'left',
        paddingTop:'5%'
     },
     wrapIconRight:{
        verticalAlign: 'middle',
        display: 'inline-flex',
        textAlign: 'left',
        paddingTop:'5%',
        paddingLeft:'15%'
     },
    bookName:{
        textAlign: 'left'
    },
    authorName:{
        textAlign: 'left',
        paddingTop:'5%'
    },
    btn:{
     width:'100%',
     color:'secondary.main'
    }
  })); 
interface Props{
    src:string,
    name:string,
    author:string,
    duration:string,
    noOfReads?:string,
    changeState?(val:number): any,
    dots?:boolean,
    button?:'add'| 'Finished' | 'Read again',
    progressBar:'none'|'full'| 'half',
    id:number
}
const innerTheme = createTheme({
  palette: {
    primary: {
      main: '#0365F2',
    },
  },
  typography:{
    button:{
      textTransform:'none'
    }
  }
});
const CardWithButton=(props:Props)=> {
  const [start, setStart]= useState(BOOKS)
    const [end, setEnd]= useState(COMPLETED)
    const classes=useStyles();
    /* const hoverButton= ()=>{
        props.variant='contained'
    } */

    const nextPage=(e: React.MouseEvent<HTMLButtonElement, MouseEvent>,val:number )=>{
      BOOKS.push({'id':2,'src':"beyondEntrepreneur.png",'name':"Beyond Entrepreneurship", 'author':"Jim Collins & Bill Lazier", 'duration':"13"})
        props.changeState && props.changeState(val)
    }

    const toRepeat=(e: React.MouseEvent<HTMLButtonElement, MouseEvent>, id:number)=>{
      console.log('to read agin')
      end.map(book=>console.log(book.name))

    }

    const toFinish=(e: React.MouseEvent<HTMLButtonElement, MouseEvent>, nameOfBook:string)=>{
     /*  console.log('finish')
      start.map(book=> console.log(book.name))

      const filter1= start.filter(book => book.name!==nameOfBook)
      console.log(filter1)
      setStart(filter1) */
      const thebook=start.filter(book => nameOfBook=== book.name )
      console.log(thebook)
      /* const changed=[...end, ...thebook]
      console.log(changed) */
      setEnd([{}])
      console.log(end)
      const currReading=start.filter(book => nameOfBook!== book.name )
      console.log(currReading)
      setStart(currReading)
      /* console.log(start) */
    }

    return (
        <div>
    <Card sx={{ maxWidth: 284 }}>
     {/* <CardMedia 
      component="img"
      height="292"
      image={props.imag} /> */}
        <Image path={props.src} width='100%' />

        <CardContent>
            <Typography className={classes.bookName} variant="subtitle1">
                {props.name}
            </Typography>

            <Typography className={classes.authorName} variant="body1">
                {props.author}
            </Typography>

              <div style={{display: 'flex', alignItems: 'center', flexWrap: 'wrap',}}>
                <Typography variant="body2" className={classes.wrapIconLeft} >
                    <AccessTimeIcon /> {props.duration}-minute read
                    </Typography>
                    {props.noOfReads &&  
                    <Typography variant="body2" className={classes.wrapIconRight} >  
                    <PersonOutlineOutlinedIcon /> {props.noOfReads}k reads </Typography>
                    }    
            </div>
        </CardContent>
      {/*  <CardActions> <Button style={{ width:'100%',color:'secondary.main'}} sx={{
    ':hover': {
      backgroundColor: 'secondary.main', // theme.palette.primary.main
      color: '#FFFFFF',}}}
      onClick={(e)=>nextPage(e,3)}  variant='text' startIcon={<AddIcon/>}>Add to library</Button></CardActions> */}
        { props.dots && <MoreHorizIcon style={{marginLeft:'88%'}}/> }
        { props.button==='add' && 
        <ThemeProvider theme={innerTheme}>
        <Button className={classes.btn} sx={{
    ':hover': {
      backgroundColor: 'primary.main', 
      color: '#FFFFFF',
    },
  }} onClick={(e)=>nextPage(e,3)} variant='text' startIcon={<AddIcon/>}>
      <Typography variant='body1'>
          Add to library
          </Typography>
      </Button>
      </ThemeProvider>}



      { props.button==='Finished' && <ThemeProvider theme={innerTheme}>
       <Button className={classes.btn} sx={{
    ':hover': {
      backgroundColor: 'primary.main', 
      color: '#FFFFFF',
    },
  }}  onClick={(e)=>toFinish(e,props.name)} variant='text'>
      <Typography variant='body1'>
          {props.button}
          </Typography>
      </Button> </ThemeProvider>}



      { props.button==='Read again'  && <ThemeProvider theme={innerTheme}>
       <Button className={classes.btn} sx={{
    ':hover': {
      backgroundColor: 'primary.main', 
      color: '#FFFFFF',
    },
  }} onClick={(e)=>toRepeat(e,props.id)} variant='text'>
      <Typography variant='body1'>
          {props.button}
          </Typography>
      </Button> </ThemeProvider>}

      {props.progressBar==='half' && <LinearProgress variant='determinate' value={30} color='info' style={{height: "10px"}}/> }

      {props.progressBar==='full' && <LinearProgress variant='determinate' value={100} color='info' style={{height: "10px"}}/> }

        </Card> 






      </div>
    );
}

export default CardWithButton;
