import React from 'react'
import {Typography,Container, Grid} from '@mui/material'
import { makeStyles } from '@mui/styles';
import Footer from './organisms/Footer/Footer';
import SwitchTab from './molecules/SwitchTab';
import Dropdown from './organisms/Dropdown/Dropdown';
import SearchBar from './molecules/SearchBar';
import Banner from './organisms/Banner/Banner';
import NewlyAdded from './utils/NewlyAdded';
import Featured from './utils/Featured';
import BookDetail from './utils/BookDetail';
import MyCard from './molecules/MyCard';

const useStyles = makeStyles(theme => ({
  mainHeading:{
    marginLeft:"15%",
    marginTop:'5%'
  },
  sideHeading:{
    paddingLeft:'10%',
    paddingTop:'3%',
    paddingBottom:'3%',
    height: '30px',
  },

})); 

function App(){
  const classes=useStyles();
  const [page,setPage]= React.useState(1)
  const changePage=(val: number)=>{
      console.log(val)
      setPage(val)
      console.log(val)
      }

  return (
    <>
    <div >
     <Dropdown changeState={changePage}/>
    </div>

{ page===1 && <div> <main  className={classes.mainHeading}>
        <Container maxWidth="lg">
        <Typography variant="h1" >
          My Library
        </Typography>
        </Container>
    </main> 

   <SwitchTab /> </div> }

  { page===2 && <div>
    <Banner/>
    <div style={{marginLeft:'20%', marginTop:'2%'}}>
    <SearchBar/>
    </div>
    <Typography className={classes.sideHeading} variant='h6'>Trending blinks</Typography>
    <Container>
      <Grid container rowSpacing={2} spacing={4}>
          <Grid item md={4}>
              <MyCard id={1} button='add' progressBar='none' src='being-boss.png' name="Being Boss" author="Kathleen Shannon and Emily..." duration="13"/>
          </Grid>

          <Grid item md={4}>
              <MyCard id={2} changeState={changePage} button='add' progressBar='none' src='beyondEntrepreneur.png' name="Beyond Entrepreneurship" author="Jim Collins & Bill Lazier" duration="12" noOfReads='1.9'/>
          </Grid>

          <Grid item md={4}>
          <MyCard  id={3} button='add' progressBar='none' src='2.png' name="Employee to Entrepreneur" author="Steve Glaveski" duration="15"  noOfReads='1.9'/>
          </Grid>

          <Grid item xs={12} sm={6} md={4}>
          <MyCard  id={4} dots={true} progressBar='half' src='4.png' name="The Fate of Food" author="Amanda Little" duration="12" noOfReads="16"/>
          </Grid>

        <Grid item xs={12} sm={6} md={4}>
        <MyCard id={5} dots={true} progressBar='half' src='5.png' name="Lives of the Stoics" author="Ryan Holiday, Stephen Hansel" duration="13"/>
        </Grid>

        <Grid item xs={12} sm={6} md={4}>
        <MyCard id={6} dots={true} progressBar='half' src='6.png' name="Loving Your Business" author="Debbie King" duration="13"/>
        </Grid>
    </Grid> 
        </Container>
    <Typography className={classes.sideHeading} variant='h6'>Just Added</Typography>
    <NewlyAdded/>
    <Typography className={classes.sideHeading} variant='h6'>Featured Audio Links</Typography>
    <Featured />
    </div>  }

{page===3 && <BookDetail changeState={changePage} />}

{page===4 && <SwitchTab />}
   <div style={{zIndex:0,backgroundColor:'#F1F6F4'}}>
    <Footer />
    </div>
    {/* <MyCard src={two} name="Bring Your Human to Work" author="Erica Keswin" duration="13" noOfReads='1.9' dots={true} progressBar='half'/>
    <br></br>
    <MyCard src={two} name="Bring Your Human to Work" author="Erica Keswin" duration="13" noOfReads='1.9' dots={false} progressBar='half' button='Finished'/>
    <br></br>
    <MyCard src={two} name="Bring Your Human to Work" author="Erica Keswin" duration="13" noOfReads='1.9' dots={false} progressBar='full' button='Read again'/>
    <br></br>
    <MyCard src={two} name="Bring Your Human to Work" author="Erica Keswin" duration="13" noOfReads='1.9' dots={false} progressBar='none' button='add'/>
    <br></br> */}
    </>
  );
}
export default App;
